RULES_ITEM.SQL_TEXT:
declare
    v_workplan_name        varchar2(4000);
    v_temp                 varchar2(4000);

    v_workplan_template_id number;

begin

    if nvl(util.getvalstrbyid(:xitor_id, id.taskdefinition.cf.tdf_propagation_options), 'NULL') not in ('WP Instantiation & Updates')
       or nvl(util.getvalnumbyid(:xitor_id, id.taskdefinition.cf.tdf_enabled), 0) = 0
    then
        return;
    end if;

    v_workplan_name := nvl(util.getvalstrbyid(:xitor_id, id.taskdefinition.cf.tdf_workplan_name), 'NULL');

    select wp_workplan_id
      into v_workplan_template_id
      from wp_workplan
     where name = v_workplan_name
       and workplan_type = 0;

    for rec in (
        select wp_workplan_id
          from wp_workplan
         where template_workplan_id = v_workplan_template_id
           and active = 1
    ) loop
        v_temp := pkg_ruleator.execute_trigger(ruletype => 30 /*After WorkPlan Create*/, tempid => v_workplan_template_id, id => rec.wp_workplan_id);
    end loop;

exception when no_data_found then
    return;
end;

declare
    oldtask            wp_tasks%rowtype;
    newtask            wp_tasks%rowtype;

    v_cur_program_id   number;
    v_task_dur_days    number;
    v_sort_ord_num     number;
    v_block_calc       number;
    v_pay_point        number;

    v_program_name     varchar2(4000);
    v_cur_program_name varchar2(4000);
    v_project_type     varchar2(4000);
    v_cur_project_type varchar2(4000);
    v_task_predecessor varchar2(4000);

begin

    for rec in (
        select distinct x.xitor_id,
               wpt.xitor_id xitor_id2,
               wpt.wp_task_id,
               wpt.finish_actual_date,
               wpt.start_projected_date,
               wpt.wp_calendar_id,
               wpt.block_calculations,
               wpt.is_not_applicable
          from config_field cf
          join xitor x on (
               x.xitor_type_id = id.taskdefinition.tt
          )
          join config_value_number cvn1 on (
               cvn1.key_value = x.xitor_id
               and cvn1.config_field_id = id.taskdefinition.cf.tdf_enabled
               and cvn1.value_number = 1
          )
          join config_value_char cvc1 on (
               cvc1.key_value = x.xitor_id
               and cvc1.config_field_id = id.taskdefinition.cf.tdf_field_name
               and cvc1.value_char = cf.config_field_name
          )
          join config_value_char cvc4 on (
               cvc4.key_value = x.xitor_id
               and cvc4.config_field_id = id.taskdefinition.cf.tdf_workplan_name
          )
          join config_value_number cvn2 on (
               cvn2.key_value = x.xitor_id
               and cvn2.config_field_id = id.taskdefinition.cf.tdf_task_name
          )
          join wp_workplan wpw on (
               wpw.template_name = cvc4.value_char
               and wpw.active = 1
               and wpw.xitor_id = :pk
          )
          join wp_tasks wpt on (
               wpt.template_task_id = cvn2.value_number
               and wpt.wp_workplan_id = wpw.wp_workplan_id
          )
        where cf.config_field_id = :id_num
          and wpt.wp_task_id is not null
    ) loop

        if nvl(util.getvalstrbyid(:pk, :id_num), 'NULL') not in (nvl(util.getvalstrbyid(rec.xitor_id, id.taskdefinition.cf.tdf_field_value), 'NULL')) then
            continue;
        end if;

        v_program_name := util.getvalstrbyid(rec.xitor_id, id.taskdefinition.cf.tdf_program_name);
        if v_program_name is not null then

            v_cur_program_id := util.getparentid(rec.xitor_id2, 'ProgramTrackor');
            if v_cur_program_id is null then
                continue;
            end if;

            v_cur_program_name := util.getvalstr(v_cur_program_id, 'XITOR_KEY');
            if instr(v_program_name, v_cur_program_name) = 0 then
                continue;
            end if;

        end if;        
        
        v_project_type := util.getvalstrbyid(rec.xitor_id, id.taskdefinition.cf.tdf_project_type);
        if v_project_type is not null then

            v_cur_project_type := util.getvalstrbyid(rec.xitor_id2, id.project.cf.p_project_type);
            if instr(v_project_type, v_cur_project_type) = 0 then
                continue;
            end if;

        end if;

        util.setvalcharbyid(rec.wp_task_id, id.tasks.cf.t_alternate_task_name, util.getvalstrbyid(rec.xitor_id, id.taskdefinition.cf.tdf_alternate_task_name));

        v_block_calc := nvl(util.getvalnumbyid(rec.xitor_id, id.taskdefinition.cf.tdf_block_calc), 0);
        if rec.block_calculations <> v_block_calc then

            --always log changes when updating wp_tasks directly
            select *
              into oldtask
              from wp_tasks
             where wp_task_id = rec.wp_task_id;

            update wp_tasks 
               set block_calculations = v_block_calc
             where wp_task_id = rec.wp_task_id;

            select *
              into newtask
              from wp_tasks
             where wp_task_id = rec.wp_task_id;

            pkg_audit.log_task_changes_full(oldtask, newtask, rec.wp_task_id, pkg_audit.c_la_update);

        end if;

        if nvl(util.getvalnumbyid(rec.xitor_id, id.taskdefinition.cf.tdf_na), 0) = 1 then
            util.SetTaskNA(rec.wp_task_id);
        end if;

        if nvl(util.getvalnumbyid(rec.xitor_id, id.taskdefinition.cf.tdf_remove_na), 0) = 1 then
            util.UnsetTaskNA(rec.wp_task_id);
        end if;

        v_task_predecessor := util.getvalstrbyid(rec.xitor_id, id.taskdefinition.cf.tdf_task_predecessor);
        if v_task_predecessor is not null then
            pkg_wp.change_preds(rec.wp_task_id, v_task_predecessor);
        end if;

        v_task_dur_days := util.getvalnumbyid(rec.xitor_id, id.taskdefinition.cf.tdf_task_duration_days);
        if v_task_dur_days is not null then

            --always log changes when updating wp_tasks directly
            select *
              into oldtask
              from wp_tasks
             where wp_task_id = rec.wp_task_id;

            update wp_tasks
               set baseline_duration = v_task_dur_days, 
                   baseline_task_window = v_task_dur_days,
                   duration = v_task_dur_days,
                   task_window = v_task_dur_days
             where wp_task_id = rec.wp_task_id;

            select *
              into newtask
              from wp_tasks
             where wp_task_id = rec.wp_task_id;

            pkg_audit.log_task_changes_full(oldtask, newtask, rec.wp_task_id, pkg_audit.c_la_update);

            if rec.finish_actual_date is null then
                util.setTaskDate(
                    TaskID => rec.wp_task_id, 
                    DateType => 'P', 
                    StartFinish => 'F',
                    Val => pkg_wp.add_x_days(
                            dt => rec.start_projected_date,
                            days => v_task_dur_days,
                            cal => rec.wp_calendar_id
                    ),
                    ForceCalcs => 1);
            end if;

        end if;

        v_sort_ord_num := util.getvalnumbyid(rec.xitor_id, id.taskdefinition.cf.tdf_sort_order_number);
        if v_sort_ord_num is not null then
            util.setvalnumbyid(rec.wp_task_id, id.tasks.cf.t_sort_order, v_sort_ord_num);
        end if;

        v_pay_point := util.getvalnumbyid(rec.xitor_id, 1000069622 /*id.taskdefinition.cf.tdf_pay_point*/);
        if v_pay_point is not null then
            util.setvalnumbyid(rec.wp_task_id, id.tasks.cf.t_pay_point, v_pay_point, 1);
        end if;

    end loop;

end;

declare
    v_found number := 0;
begin

    delete from rule_id_num
    where rule_id = 100007144 /*Trigger Fields updated recalculate Task Definition*/;
    
    for cur in (
        select distinct cf.config_field_id
        from
            xitor x
            join config_value_number cvn on (
                cvn.key_value = x.xitor_id and
                cvn.config_field_id = 1000065345 /*TDF_ENABLED*/ and
                cvn.value_number = 1
            )
            join config_value_char cvc on (
                cvc.key_value = x.xitor_id and
                cvc.config_field_id = 1000063640 /*TDF_FIELD_NAME*/ and
                cvc.value_char is not null
            )
            join config_field cf on (cf.config_field_name = cvc.value_char)
        where x.xitor_type_id = 100006805 /*Task Definition*/
    ) loop
        insert into rule_id_num (rule_id, id_num, program_id)
        values (100007144 /*Trigger Fields updated recalculate Task Definition*/, cur.config_field_id, pkg_sec.get_pid());
        v_found := 1;
    end loop;

    if v_found = 0 then
        update rule
        set is_enabled = 0
        where rule_id = 100007144 /*Trigger Fields updated recalculate Task Definition*/;
    elsif v_found = 1 then
        update rule
        set is_enabled = 1
        where rule_id = 100007144 /*Trigger Fields updated recalculate Task Definition*/;
    end if;

end;

begin

    if :id_num in (id.project.cf.p_project_type, id.project.cf.p_workplan_task_due_timeframe) then
        PKG_TASK_DEFINITION.set_workplan_task_due(:pk);
    elsif :id_num in (id.taskdefinition.cf.tdf_project_type, id.taskdefinition.cf.tdf_include_workplan_task_due, id.taskdefinition.cf.tdf_task_name, id.taskdefinition.cf.tdf_program_name) then
        for rec in (
            select xitor_id
              from xitor
             where xitor_type_id = id.project.tt
        ) loop
            PKG_TASK_DEFINITION.set_workplan_task_due(rec.xitor_id);
        end loop;
    end if;

end;

DB_PACKAGES_ITEM.DB_PACKAGE_SPEC:
CREATE OR REPLACE EDITIONABLE PACKAGE "PKG_TASK_DEFINITION" as

    procedure update_tasks_tdf(p_workplan_id number);
    procedure set_workplan_task_due(p_project_id number);

end PKG_TASK_DEFINITION;

DB_PACKAGES_ITEM.DB_PACKAGE_BODY:
CREATE OR REPLACE EDITIONABLE PACKAGE BODY "PKG_TASK_DEFINITION" as

    procedure update_tasks_tdf (p_workplan_id number) is
        v_name varchar2(4000);
        v_x_id number;
        v_wt_id number;
        v_cur_program_id number;
        v_cur_program_name varchar2(4000);
        v_cur_project_type varchar2(4000);
        v_is_na number;
        v_old_task wp_tasks%rowtype;
        v_new_task wp_tasks%rowtype;
    begin

        select wtp.name, wp.xitor_id
          into v_name, v_x_id
          from wp_workplan wp
          join wp_workplan wtp on wp.template_workplan_id = wtp.wp_workplan_id
         where wp.wp_workplan_id = p_workplan_id;
        if v_x_id is null or v_name is null then return; end if;

        v_cur_program_id := util.getparentid(v_x_id, 'ProgramTrackor');
        if v_cur_program_id is not null then
            v_cur_program_name := util.getvalstr(v_cur_program_id, 'XITOR_KEY');
        end if;
        v_cur_project_type := util.getvalstrbyid(v_x_id, id.project.cf.p_project_type);

        for rec in (
            select util.getvalnumbyid(wtd.xitor_id, id.taskdefinition.cf.tdf_task_name) tdf_task_name,
                   util.getvalnumbyid(wtd.xitor_id, id.taskdefinition.cf.tdf_task_duration_days) tdf_task_duration_days,
                   util.getvalstrbyid(wtd.xitor_id, id.taskdefinition.cf.tdf_task_predecessor) tdf_task_predecessor,
                   util.getvalnumbyid(wtd.xitor_id, id.taskdefinition.cf.tdf_sort_order_number) tdf_sort_order_number,
                   nvl(util.getvalnumbyid(wtd.xitor_id, id.taskdefinition.cf.tdf_na), 0) tdf_na,
                   nvl(util.getvalnumbyid(wtd.xitor_id, id.taskdefinition.cf.tdf_remove_na), 0) tdf_remove_na,
                   nvl(util.getvalnumbyid(wtd.xitor_id, id.taskdefinition.cf.tdf_block_calc), 0) tdf_block_calc,
                   util.getvalstrbyid(wtd.xitor_id, id.taskdefinition.cf.tdf_alternate_task_name) tdf_alternate_task_name,
                   cfn.value_char tdf_field_name,
                   cfv.value_char tdf_field_value,
                   replace(util.getvalstrbyid(wtd.xitor_id, id.taskdefinition.cf.tdf_program_name), 'amp;') tdf_program_name,
                   replace(util.getvalstrbyid(wtd.xitor_id, id.taskdefinition.cf.tdf_project_type), 'amp;') tdf_project_type,
                   wtd.xitor_id
              from xitor wtd
              join config_value_char c on
                  wtd.xitor_id = c.key_value
                  and c.config_field_id = id.taskdefinition.cf.tdf_workplan_name
              left join config_value_char cfn on
                  wtd.xitor_id = cfn.key_value
                  and cfn.config_field_id = id.taskdefinition.cf.tdf_field_name
              left join config_value_char cfv on
                  wtd.xitor_id = cfv.key_value
                  and cfv.config_field_id = id.taskdefinition.cf.tdf_field_value
              join config_value_number cvn on
                  wtd.xitor_id = cvn.key_value 
                  and cvn.config_field_id = id.taskdefinition.cf.tdf_enabled
                  and nvl(cvn.value_number, 0) = 1
             where wtd.xitor_type_id = id.taskdefinition.tt
               and c.value_char = v_name
        ) loop

            if (rec.tdf_field_name is null and rec.tdf_program_name is null and rec.tdf_project_type is null)
            or (rec.tdf_field_name is not null and util.getvalstr(v_x_id, rec.tdf_field_name) <> rec.tdf_field_value)
            or (rec.tdf_program_name is not null and instr(rec.tdf_program_name, v_cur_program_name) = 0)
            or (rec.tdf_project_type is not null and instr(rec.tdf_project_type, v_cur_project_type) = 0)
            then
                continue;
            end if;
            begin  
                select wp_task_id into v_wt_id from wp_tasks where wp_workplan_id = p_workplan_id and template_task_id = rec.tdf_task_name;
            exception when no_data_found then
                continue;
            end;
            select * into v_old_task from wp_tasks where wp_task_id = v_wt_id;
            util.setvalcharbyid(v_wt_id, id.tasks.cf.t_alternate_task_name, rec.tdf_alternate_task_name);
            update wp_tasks set block_calculations = rec.tdf_block_calc where wp_task_id = v_wt_id;

            if rec.tdf_na = 1 or rec.tdf_remove_na = 1 or rec.tdf_task_duration_days is not null then
                --!!!don't n/a first task of workplan!!!
                select is_not_applicable into v_is_na from wp_tasks where wp_task_id = v_wt_id;
                if v_is_na = 0 and rec.tdf_na = 1 then
                    update wp_tasks 
                       set is_required = 0,
                           is_not_applicable = 1,
                           baseline_duration = 0,
                           baseline_task_window = 0,
                           duration = 0,
                           task_window = 0,
                           baseline_duration_na_store = duration,
                           baseline_task_window_na_store = task_window,
                           duration_na_store = duration,
                           task_window_na_store = task_window
                     where wp_task_id = v_wt_id;
                elsif v_is_na = 1 and rec.tdf_remove_na = 1 then
                    update wp_tasks 
                       set is_not_applicable = 0,
                           baseline_duration = baseline_duration_na_store,
                           baseline_task_window = baseline_task_window_na_store,
                           duration = duration_na_store,
                           task_window = task_window_na_store
                     where wp_task_id = v_wt_id;
                end if;
                if rec.tdf_task_duration_days is not null and v_is_na = 1 then
                    update wp_tasks 
                       set baseline_duration_na_store = rec.tdf_task_duration_days, 
                           baseline_task_window_na_store = rec.tdf_task_duration_days,
                           duration_na_store = rec.tdf_task_duration_days, 
                           task_window_na_store = rec.tdf_task_duration_days
                     where wp_task_id = v_wt_id;
                elsif rec.tdf_task_duration_days is not null and v_is_na = 0 then
                    update wp_tasks 
                       set baseline_duration = rec.tdf_task_duration_days, 
                           baseline_task_window = rec.tdf_task_duration_days,
                           duration = rec.tdf_task_duration_days, 
                           task_window = rec.tdf_task_duration_days
                     where wp_task_id = v_wt_id;
                end if;
            end if;

            select * into v_new_task from wp_tasks where wp_task_id = v_wt_id;
            pkg_audit.log_task_changes_full(v_old_task, v_new_task, v_wt_id, pkg_audit.c_la_update);

            if rec.tdf_task_predecessor is not null then
                pkg_wp.change_preds(v_wt_id, rec.tdf_task_predecessor);
            end if;
            if rec.tdf_sort_order_number is not null then
                util.setvalnumbyid(v_wt_id, id.tasks.cf.t_sort_order, rec.tdf_sort_order_number);
            end if;

        end loop;

    exception when no_data_found then
        util.raiseerror('Task Definition Package No Data Found Error!');
    end update_tasks_tdf;

    procedure set_workplan_task_due(p_project_id number)
    is
        v_project_type       number;
        v_program_id         number;
        v_task_due_number    number;

        v_task_due_timeframe varchar2(4000);
        v_task_due           varchar2(4000);

    begin

        v_task_due_timeframe := nvl(util.getvalstrbyid(p_project_id, id.project.cf.p_workplan_task_due_timeframe), 'N/A');
        if v_task_due_timeframe = 'N/A' then
            util.setvalcharbyid(p_project_id, id.project.cf.p_workplan_task_due, null);
            return;

        end if;

        if v_task_due_timeframe = 'Past Due' then
            v_task_due_number := 0;
        else
            v_task_due_number := to_number(v_task_due_timeframe);
        end if;

        v_project_type := util.getvalnumbyid(p_project_id, id.project.cf.p_project_type);
        v_program_id   := util.getparentid(p_project_id, 'ProgramTrackor');

        for rec in (
            select distinct wpt.order_number || ' ' || wpt.task_name task_due
              from wp_workplan wpw
              join wp_tasks wpt on (
                   wpt.wp_workplan_id = wpw.wp_workplan_id
                   and wpt.finish_actual_date is null
                   and wpt.is_not_applicable = 0
                   and wpt.finish_projected_date - v_task_due_number < current_date
              )
              join xitor x on (x.xitor_type_id = id.taskdefinition.tt)
              join config_value_number cvn1 on (
                   cvn1.key_value = x.xitor_id
                   and cvn1.config_field_id = id.taskdefinition.cf.tdf_include_workplan_task_due
                   and nvl(cvn1.value_number, 0) = 1
              )
              join config_value_number cvn2 on (
                   cvn2.key_value = x.xitor_id
                   and cvn2.config_field_id = id.taskdefinition.cf.tdf_task_name
                   and nvl(cvn2.value_number, 0) = wpt.template_task_id
              )
              left join config_value_mult cvm1 on (
                   cvm1.key_value = x.xitor_id
                   and cvm1.config_field_id = id.taskdefinition.cf.tdf_program_name
              )
              left join config_value_mult cvm2 on (
                   cvm2.key_value = x.xitor_id
                   and cvm2.config_field_id = id.taskdefinition.cf.tdf_project_type
              )
             where wpw.active = 1
               and wpw.xitor_id = p_project_id
               and (cvm1.value_number is null or cvm1.value_number = v_program_id)
               and (cvm2.value_number is null or cvm2.value_number = v_project_type)
        ) loop
            v_task_due := v_task_due || rec.task_due || ', ';
        end loop;

        if v_task_due is not null then
            v_task_due := substr(v_task_due, 0, instr(v_task_due, ',', -1) - 1);
        end if;

        util.setvalcharbyid(p_project_id, id.project.cf.p_workplan_task_due, v_task_due);

    end set_workplan_task_due;

end PKG_TASK_DEFINITION;
